    public class WrappedToken
    {
        public static async Task<string> DeployContractAsync(Web3 web3, BigInteger totalSupply)
        {
            var deploymentMessage = new WrappedTokenDeployment()
            {
                InitialSupply = totalSupply
            };

            IContractDeploymentTransactionHandler<WrappedTokenDeployment> deploymentHandler = web3.Eth.GetContractDeploymentHandler<WrappedTokenDeployment>();
            TransactionReceipt transactionReceiptDeployment = await deploymentHandler.SendRequestAndWaitForReceiptAsync(deploymentMessage).ConfigureAwait(false);
            string contractAddress = transactionReceiptDeployment.ContractAddress;

            return contractAddress;
        }

        public static async Task<BigInteger> GetErc20BalanceAsync(Web3 web3, string contractAddress, string addressToQuery)
        {
            var balanceOfFunctionMessage = new BalanceOfFunction()
            {
                Owner = addressToQuery
            };

            IContractQueryHandler<BalanceOfFunction> balanceHandler = web3.Eth.GetContractQueryHandler<BalanceOfFunction>();
            BigInteger balance = await balanceHandler.QueryAsync<BigInteger>(contractAddress, balanceOfFunctionMessage).ConfigureAwait(false);

            return balance;
        }

        public static async Task<string> TransferAsync(Web3 web3, string contractAddress, string recipient, BigInteger amount, BigInteger gas, BigInteger gasPrice)
        {
            IContractTransactionHandler<TransferFunction> transferHandler = web3.Eth.GetContractTransactionHandler<TransferFunction>();

            var transfer = new TransferFunction()
            {
                To = recipient,
                TokenAmount = amount,
                Gas = gas,
                GasPrice = Web3.Convert.ToWei(gasPrice, UnitConversion.EthUnit.Gwei)
            };

            TransactionReceipt transactionTransferReceipt = await transferHandler.SendRequestAndWaitForReceiptAsync(contractAddress, transfer).ConfigureAwait(false);

            return transactionTransferReceipt.TransactionHash;
        }

        public static async Task<string> TransferOfflineAsync(Web3 web3, string contractAddress, string recipient, BigInteger amount, HexBigInteger nonce, BigInteger gas, BigInteger gasPrice, string fromAddress = null)
        {
            IContractTransactionHandler<TransferFunction> transferHandler = web3.Eth.GetContractTransactionHandler<TransferFunction>();

            var transfer = new TransferFunction
            {
                To = recipient,
                TokenAmount = amount,
                // Nethereum internally calls its Ethereum client by default to set the GasPrice, Nonce and estimate the Gas, 
                // so if we want to sign the transaction for the contract completely offline we will need to set those values ourselves.
                Nonce = nonce.Value,
                Gas = gas,
                GasPrice = Web3.Convert.ToWei(gasPrice, UnitConversion.EthUnit.Gwei)
            };

            if (fromAddress != null)
                transfer.FromAddress = fromAddress;

            string result = await transferHandler.SignTransactionAsync(contractAddress, transfer).ConfigureAwait(false);

            return result;
        }

        public static async Task<string> TransferOwnershipAsync(Web3 web3, string contractAddress, string newOwner)
        {
            IContractTransactionHandler<TransferOwnershipFunction> transferHandler = web3.Eth.GetContractTransactionHandler<TransferOwnershipFunction>();

            var transfer = new TransferOwnershipFunction()
            {
                NewOwner = newOwner
            };

            TransactionReceipt transactionTransferReceipt = await transferHandler.SendRequestAndWaitForReceiptAsync(contractAddress, transfer).ConfigureAwait(false);

            return transactionTransferReceipt.TransactionHash;
        }

        public static async Task<string> GetOwnerAsync(Web3 web3, string contractAddress)
        {
            var ownerFunctionMessage = new OwnerFunction()
            {
            };

            IContractQueryHandler<OwnerFunction> ownerHandler = web3.Eth.GetContractQueryHandler<OwnerFunction>();
            string owner = await ownerHandler.QueryAsync<string>(contractAddress, ownerFunctionMessage).ConfigureAwait(false);

            return owner;
        }

        public static async Task<string> GetDestinationAddressAsync(Web3 web3, string contractAddress, string addressToQuery)
        {
            var withdrawalAddressesFunctionMessage = new WithdrawalAddressesFunction()
            {
                Address = addressToQuery
            };

            IContractQueryHandler<WithdrawalAddressesFunction> queryHandler = web3.Eth.GetContractQueryHandler<WithdrawalAddressesFunction>();
            string destinationAddress = await queryHandler.QueryAsync<string>(contractAddress, withdrawalAddressesFunctionMessage).ConfigureAwait(false);

            return destinationAddress;
        }

