using System;
using System.Collections.Generic;
using System.Numerics;
using System.Threading.Tasks;
using Nethereum.ABI.FunctionEncoding.Attributes;
using Nethereum.Contracts;
using Nethereum.Contracts.ContractHandlers;
using Nethereum.Contracts.CQS;
using Nethereum.RPC.Eth.DTOs;
using Nethereum.Web3;
using Stratis.Bitcoin.Features.SmartContracts.Interop.Models;

namespace Stratis.Bitcoin.Features.SmartContracts.Interop
{
    public class EthereumClient
    {
        private readonly Uri baseUrl;
        private readonly Web3 web3;
        private readonly string account;
        private readonly string passphrase;

        public EthereumClient(Uri baseUrl, string account, string passphrase)
        {
            this.baseUrl = baseUrl;
            this.account = account;
            this.passphrase = passphrase;

            // TODO: Support loading offline accounts from keystore JSON?
            if (baseUrl != null)
                this.web3 = new Web3(baseUrl.ToString());
            else
                this.web3 = new Web3();
        }
        
        public class WrappedStraxTokenDeployment : ContractDeploymentMessage
        {
            public static string BYTECODE =
                "";

            public WrappedStraxTokenDeployment() : base(BYTECODE)
            {
            }

            [Parameter("uint256", "totalSupply")]
            public BigInteger TotalSupply { get; set; }
        }

        [Function("balanceOf", "uint256")]
        public class BalanceOfFunction : FunctionMessage
        {
            [Parameter("address", "_owner", 1)]
            public string Owner { get; set; }
        }

        [FunctionOutput]
        public class BalanceOfOutputDTO : IFunctionOutputDTO
        {
            [Parameter("uint256", "balance", 1)]
            public BigInteger Balance { get; set; }
        }

        [Function("transfer", "bool")]
        public class TransferFunction : FunctionMessage
        {
            [Parameter("address", "_to", 1)]
            public string To { get; set; }

            [Parameter("uint256", "_value", 2)]
            public BigInteger TokenAmount { get; set; }
        }

        public class MultisigWalletDeployment : ContractDeploymentMessage
        {
            public static string BYTECODE =
                "";

            public MultisigWalletDeployment() : base(BYTECODE)
            {
            }

            [Parameter("address[]", "_owners", 1)]
            public string[] Owners { get; set; }

            [Parameter("uint", "_required", 2)]
            public uint Required { get; set; }
        }

        public async Task<string> DeployWrappedStraxContract(BigInteger totalSupply)
        {
            var deploymentMessage = new WrappedStraxTokenDeployment()
            {
                TotalSupply = totalSupply
            };

            IContractDeploymentTransactionHandler<WrappedStraxTokenDeployment> deploymentHandler = this.web3.Eth.GetContractDeploymentHandler<WrappedStraxTokenDeployment>();
            TransactionReceipt transactionReceiptDeployment = await deploymentHandler.SendRequestAndWaitForReceiptAsync(deploymentMessage);
            string contractAddress = transactionReceiptDeployment.ContractAddress;

            return contractAddress;
        }

        public async Task<string> DeployMultisigWalletContract(string[] owners, uint required)
        {
            var deploymentMessage = new MultisigWalletDeployment()
            {
                Owners = owners,
                Required = required
            };

            IContractDeploymentTransactionHandler<MultisigWalletDeployment> deploymentHandler = this.web3.Eth.GetContractDeploymentHandler<MultisigWalletDeployment>();
            TransactionReceipt transactionReceiptDeployment = await deploymentHandler.SendRequestAndWaitForReceiptAsync(deploymentMessage);
            string contractAddress = transactionReceiptDeployment.ContractAddress;

            return contractAddress;
        }

        public async Task<BigInteger> GetErc20Balance(string contractAddress, string addressToQuery)
        {
            var balanceOfFunctionMessage = new BalanceOfFunction()
            {
                Owner = addressToQuery
            };

            IContractQueryHandler<BalanceOfFunction> balanceHandler = this.web3.Eth.GetContractQueryHandler<BalanceOfFunction>();
            BigInteger balance = await balanceHandler.QueryAsync<BigInteger>(contractAddress, balanceOfFunctionMessage);

            return balance;
        }

        public async Task<string> Transfer(string contractAddress, string recipient, BigInteger amount)
        {
            IContractTransactionHandler<TransferFunction> transferHandler = this.web3.Eth.GetContractTransactionHandler<TransferFunction>();

            var transfer = new TransferFunction()
            {
                To = recipient,
                TokenAmount = amount
            };

            TransactionReceipt transactionTransferReceipt = await transferHandler.SendRequestAndWaitForReceiptAsync(contractAddress, transfer);

            return transactionTransferReceipt.TransactionHash;
        }

        public Dictionary<string, string> InvokeContract(InteropRequest request)
        {
            // TODO: Create an Ethereum transaction to invoke the specified method 
            return new Dictionary<string, string>();
        }

        public List<EthereumRequestModel> GetStratisInteropRequests()
        {
            // TODO: Filter the receipt logs for the interop contract and extract
            return new List<EthereumRequestModel>();
        }

        public bool TransmitResponse(InteropRequest request)
        {
            // TODO: Send a transaction to the interop contract recording the results of the execution on the Cirrus network
            return true;
        }
    }
}
